import React from 'react';
import { useSelector } from 'react-redux';
import {
  Box,
  Card,
  CardContent,
  Typography,
  CircularProgress,
  Alert,
} from '@mui/material';

const PartnerListPage = () => {
  const { list, status, error } = useSelector((state) => state.users);

  if (status === 'loading') return <CircularProgress sx={{ m: 3 }} />;
  if (error) return <Alert severity="error">{error}</Alert>;
  if (status === 'succeeded' && list.length === 0)
    return <Typography sx={{ m: 3 }}>No partners found.</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      {list.map((user) => (
        <Card key={user.id} sx={{ mb: 2 }}>
          <CardContent>
            <Typography variant="h6">{user.full_name}</Typography>
            <Typography>Native: {user.UserLanguages[0].type}</Typography>
            <Typography>Target: {user.UserLanguages?.map((lang) => `${lang.Language.name} is ${lang.Language.type}`)}</Typography>
          </CardContent>
        </Card>
      ))}
    </Box>
  );
};

export default PartnerListPage;
