import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchUsers } from '../services/userService';
import {
    Box,
    Button,
    FormControl,
    InputLabel,
    MenuItem,
    Select,
    Typography,
} from '@mui/material';


const PartnerSearchForm = () => {
    const availableLanguages = useSelector((state) => state.users.allLang);

    const [native, setNative] = useState('');
    const [target, setTarget] = useState('');

    const toSendNative =  (lang) => availableLanguages.find((obj) => obj.name === lang).id

    const toSendTarget = targetLanguages.map(
        (lang) => availableLanguages.find((obj) => obj.name === lang).id
    );

    const dispatch = useDispatch();

    const handleSubmit = (e) => {
        e.preventDefault();
        dispatch(fetchUsers({ native, target }));
    };

    return (
        <Box component='form' onSubmit={handleSubmit} sx={{ p: 3 }}>
            <Typography variant='h6' gutterBottom>
                Find Language Partners
            </Typography>

            <FormControl fullWidth margin='normal'>
                <InputLabel>Native Language</InputLabel>
                <Select
                    value={native}
                    onChange={(e) => setNative(e.target.value)}
                    label='Native Language'>
                    {availableLanguages.map((lang) => (
                        <MenuItem key={lang.id} value={lang.name}>
                            {lang.name}
                        </MenuItem>
                    ))}
                </Select>
            </FormControl>

            <FormControl fullWidth margin='normal'>
                <InputLabel>Target Language</InputLabel>
                <Select
                    value={target}
                    onChange={(e) => setTarget(e.target.value)}
                    label='Target Language'>
                     {availableLanguages.map((lang) => (
                        <MenuItem key={lang.id} value={lang.name}>
                            {lang.name}
                        </MenuItem>
                    ))}
                </Select>
            </FormControl>

            <Button variant='contained' type='submit' sx={{ mt: 2 }}>
                Search
            </Button>
        </Box>
    );
};

export default PartnerSearchForm;
