import React from 'react';
import { useSelector } from 'react-redux';
import {
    Container,
    Box,
    Typography,
    Card,
    CardContent,
    Chip,
    Button,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const ProfilePage = () => {
    const user = useSelector((state) => state.auth.user);
    const navigate = useNavigate();

    if (!user) {
        return (
            <Container>
                <Box sx={{ p: 3 }}>
                    <Typography variant='h6'>You are not logged in.</Typography>
                    <Button
                        variant='contained'
                        onClick={() => navigate('/login')}
                        sx={{ mt: 2 }}>
                        Login
                    </Button>
                </Box>
            </Container>
        );
    }

    return (
        <Box sx={{ p: 3 }}>
            <Typography variant='h5' gutterBottom>
                My Profile
            </Typography>

            <Card>
                <CardContent>
                    <Typography variant='h6'>{user.username}</Typography>
                    <Typography>Email: {user.email}</Typography>

                    <Box sx={{ mt: 2 }}>
                        <Typography variant='subtitle1'>
                            Native Languages:
                        </Typography>
                        {user.nativeLanguages?.map((lang) => (
                            <Chip
                                key={lang}
                                label={lang}
                                sx={{ mr: 1, mt: 1 }}
                            />
                        ))}
                    </Box>

                    <Box sx={{ mt: 2 }}>
                        <Typography variant='subtitle1'>
                            Target Languages:
                        </Typography>
                        {user.targetLanguages?.map((lang) => (
                            <Chip
                                key={lang}
                                label={lang}
                                color='primary'
                                sx={{ mr: 1, mt: 1 }}
                            />
                        ))}
                    </Box>
                </CardContent>
            </Card>
        </Box>
    );
};

export default ProfilePage;
