import { createSlice } from '@reduxjs/toolkit';
import { createReq } from '../services/requestService';

const requestsSlice = createSlice({
    name: 'reqs',
    initialState: {
        list: [],
        status: '',
        error: null,
    },
    reducers: {
        clearReqs: (state) => {
            state.list = [];
            state.status = ' ';
            state.error = null;
        },
    },
    extraReducers: (builder) => {
        builder
            .addCase(createReq.pending, (state) => {
                state.status = 'loading';
                state.error = null;
            })

    },
});

export const { clearUsers } = requestsSlice.actions;
export default requestsSlice.reducer;
