import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
    Box,
    Typography,
    Card,
    CardContent,
    Button,
    Autocomplete,
    TextField,
    Snackbar,
    Alert,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { getMe } from '../services/userService';

const availableLanguages = [
    'English',
    'Spanish',
    'French',
    'German',
    'Italian',
    'Russian',
    'Japanese',
    'Chinese',
    'Arabic',
    'Portuguese',
];

const ProfilePage = () => {
    const user = useSelector((state) => state.users.list);
    const native = user.languages?.filter(
        (language) => language.type === 'native'
    );


    const target = user.languages?.filter(
        (language) => language.type === 'target'
    );
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const [nativeLanguages, setNativeLanguages] = useState(native || []);
    const [targetLanguages, setTargetLanguages] = useState(target || []);
    const [success, setSuccess] = useState(false);

    if (!user) {
        return (
            <Box sx={{ p: 3 }}>
                <Typography variant='h6'>You are not logged in.</Typography>
                <Button
                    variant='contained'
                    onClick={() => navigate('/login')}
                    sx={{ mt: 2 }}>
                    Login
                </Button>
            </Box>
        );
    }

    const handleSave = async () => {
        try {
            const response = await axios.put(
                `http://localhost:3001/users/me`,
                {
                    nativeLanguages,
                    targetLanguages,
                },
                {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem(
                            'token'
                        )}`,
                    },
                }
            );

            dispatch(getMe(response.data)); // update user in Redux
            setSuccess(true);
        } catch (error) {
            console.error('Update failed:', error);
        }
    };

    return (
        <Box sx={{ p: 3 }}>
            <Typography variant='h5' gutterBottom>
                My Profile
            </Typography>

            <Card>
                <CardContent>
                    <Typography variant='h6'>{user.username}</Typography>
                    <Typography>Email: {user.email}</Typography>

                    <Box sx={{ mt: 3 }}>
                        <Typography variant='subtitle1'>
                            Native Languages:
                        </Typography>
                        <Autocomplete
                            multiple
                            options={availableLanguages}
                            value={nativeLanguages}
                            onChange={(e, value) => setNativeLanguages(value)}
                            renderInput={(params) => (
                                <TextField
                                    {...params}
                                    label='Select native languages'
                                />
                            )}
                            sx={{ mt: 1 }}
                        />
                    </Box>

                    <Box sx={{ mt: 3 }}>
                        <Typography variant='subtitle1'>
                            Target Languages:
                        </Typography>
                        <Autocomplete
                            multiple
                            options={availableLanguages}
                            value={targetLanguages}
                            onChange={(e, value) => setTargetLanguages(value)}
                            renderInput={(params) => (
                                <TextField
                                    {...params}
                                    label='Select target languages'
                                />
                            )}
                            sx={{ mt: 1 }}
                        />
                    </Box>

                    <Button
                        variant='contained'
                        onClick={handleSave}
                        sx={{ mt: 3 }}>
                        Save Changes
                    </Button>
                </CardContent>
            </Card>

            <Snackbar
                open={success}
                autoHideDuration={3000}
                onClose={() => setSuccess(false)}>
                <Alert
                    onClose={() => setSuccess(false)}
                    severity='success'
                    sx={{ width: '100%' }}>
                    Profile updated!
                </Alert>
            </Snackbar>
        </Box>
    );
};

export default ProfilePage;
