import { useSelector, useDispatch } from 'react-redux';
import { getAllLanguages } from '../services/languageControlService';
import { useEffect, useState } from 'react';
import {
    Container,
    Box,
    Typography,
    Chip,
    TextField,
    Button,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
} from '@mui/material';
import { addLanguage } from '../services/languageControlService';

const LanguageControlPage = () => {
    const dispatch = useDispatch();
    const { languages, status, error } = useSelector(
        (state) => state.langControl
    );

    const [newLanguageName, setNewLanguageName] = useState('');
    const [delLanguageName, setDelLanguageName] = useState('');

    useEffect(() => {
        dispatch(getAllLanguages());
    }, [dispatch]);

    const handleAddSubmit = (e) => {
        e.preventDefault();
        dispatch(addLanguage(newLanguageName));
    };

    return (
        <Container maxWidth='sm'>
            <Box mt={5}>
                <Typography variant='h4' gutterBottom>
                    Languages
                </Typography>
                {status === 'loading' && (
                    <Typography variant='h6' gutterBottom>
                        Loading...
                    </Typography>
                )}
                {status === 'failed' && (
                    <Typography variant='h6' gutterBottom>
                        Error: {error}
                    </Typography>
                )}
                {status === 'succeeded' && (
                    <Box>
                        {languages.map((language) => (
                            <Chip key={language.id} label={language.name} />
                        ))}
                    </Box>
                )}
            </Box>
            <Box mt={5}>
                <Typography variant='h6' gutterBottom>
                    Add Language
                </Typography>
                <form onSubmit={handleAddSubmit}>
                    <TextField
                        fullWidth
                        label='Language Name'
                        margin='normal'
                        value={newLanguageName}
                        onChange={(e) => setNewLanguageName(e.target.value)}
                        required
                    />
                    <Button
                        type='submit'
                        variant='contained'
                        color='primary'
                        fullWidth
                        disabled={status === 'loading'}>
                        {status === 'loading' ? 'Logging in...' : 'Add'}
                    </Button>
                </form>
            </Box>
            <form onSubmit={handleDelSubmit}>
                <Box mt={5} >
                    <Typography variant='h6' gutterBottom>
                        Delete Language
                    </Typography>
                    <FormControl fullWidth margin='normal'>
                        <InputLabel>Select Language</InputLabel>
                        <Select
                            value={delLanguageName}
                            onChange={(e) => setDelLanguageName(e.target.value)}
                            label='Select Language'
                            required>
                            {languages.map((lang) => (
                                <MenuItem key={lang.id} value={lang.id}>
                                    {lang.name}
                                </MenuItem>
                            ))}
                        </Select>
                    </FormControl>
                    <Button
                        fullWidth
                        variant='contained'
                        color='warning'
                        type='submit'>
                        Delete
                    </Button>
                </Box>
            </form>
        </Container>
    );
};

export default LanguageControlPage;
