import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';
import axios from 'axios';

const API_URL = 'http://localhost:3333';
// Fetch users by language match
export const fetchUsers = createAsyncThunk(
    'users/fetchUsers',
    async ({ native, target }, { rejectWithValue, getState }) => {
        try {
            const { token } = getState().auth;
            const response = await axios.get(`${API_URL}/api/users`, {
                params: { native, target },
                headers: {
                    Authorization: `Bearer ${token}`,
                },
            });
            return response.data; // expected to be an array of users
        } catch (err) {
            return rejectWithValue(err.response?.data?.message || err.message);
        }
    }
);

export const getMe = createAsyncThunk('users/me', async (token, thunkAPI) => {
    try {
        const res = await axios.get(`${API_URL}/api/users/me`, {
            headers: { Authorization: `Bearer ${token}` },
        });

        return res.data;
    } catch (err) {
        return thunkAPI.rejectWithValue(
            err.response.data.message || 'Login failed'
        );
    }
});

const usersSlice = createSlice({
    name: 'users',
    initialState: {
        list: [],
        status: 'idle', // 'idle' | 'loading' | 'succeeded' | 'failed'
        error: null,
    },
    reducers: {
        clearUsers: (state) => {
            state.list = [];
            state.status = 'idle';
            state.error = null;
        },
    },
    extraReducers: (builder) => {
        builder
            .addCase(fetchUsers.pending, (state) => {
                state.status = 'loading';
                state.error = null;
            })
            .addCase(fetchUsers.fulfilled, (state, action) => {
                state.status = 'succeeded';
                state.list = action.payload;
            })
            .addCase(fetchUsers.rejected, (state, action) => {
                state.status = 'failed';
                state.error = action.payload;
            });
    },
});

export const { clearUsers } = usersSlice.actions;
export default usersSlice.reducer;
