import React from 'react';
import { useSelector } from 'react-redux';
import {
    Container,
    Box,
    Typography,
    Card,
    CardContent,
    Chip,
    Button,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';

const ProfilePage = () => {
    const user = useSelector((state) => state.users.list);
    const navigate = useNavigate();

    if (!user) {
        return (
            <Container maxWidth='sm'>
                <Box sx={{ p: 3 }}>
                    <Typography variant='h6'>You are not logged in.</Typography>
                    <Button
                        variant='contained'
                        onClick={() => navigate('/login')}
                        sx={{ mt: 2 }}>
                        Login
                    </Button>
                </Box>
            </Container>
        );
    }

    return (
        <Box sx={{ p: 3 }}>
            <Typography variant='h5' gutterBottom>
                My profile
            </Typography>

            <Card>
                <CardContent>
                    <Typography variant='h6'>{user.full_name}</Typography>
                    <Button
                        variant='outlined'
                        onClick={() => navigate('/search')}
                        sx={{ mt: 1 }}>
                        Edit
                    </Button>
                    <Box sx={{ mt: 2 }}>
                        <Typography variant='subtitle1'>
                            Native Languages:
                        </Typography>
                        {user.languages
                            ?.filter((lang) => lang.type === 'native')
                            .map((lang) => (
                                <Chip
                                    key={lang.language_id}
                                    label={lang.name}
                                    sx={{ mr: 1, mt: 1 }}
                                />
                            ))}
                    </Box>

                    <Box sx={{ mt: 2 }}>
                        <Typography variant='subtitle1'>
                            Target Languages:
                        </Typography>
                        {user.languages
                            ?.filter((lang) => lang.type === 'target')
                            .map((lang) => (
                                <Chip
                                    key={lang.language_id}
                                    label={lang.name}
                                    sx={{ mr: 1, mt: 1 }}
                                />
                            ))}
                    </Box>
                </CardContent>
            </Card>
            <Button
                variant='contained'
                onClick={() => navigate('/search')}
                sx={{ mt: 2 }}>
                Search Partners
            </Button>
        </Box>
    );
};

export default ProfilePage;
