import { useSelector, useDispatch } from 'react-redux';
import { getAllLanguages } from '../services/languageControlService';
import { useEffect, useState } from 'react';
import { Container, Box, Typography, Chip, TextField , Button, FormControl, } from '@mui/material';
import { addLanguage } from '../services/languageControlService';

const LanguageControlPage = () => {
    const dispatch = useDispatch();
    const { languages, status, error } = useSelector((state) => state.langControl);

    const [languageName, setLanguageName] = useState('');

    useEffect(() => {
        dispatch(getAllLanguages());
    }, [dispatch]);

    const handleSubmit = (e) => {
        e.preventDefault();
        dispatch(addLanguage(languageName));
    };


    return (
        <Container maxWidth='sm'>
            <Box mt={5}>
                <Typography variant='h4' gutterBottom>
                    Languages
                </Typography>
                {status === 'loading' && <Typography variant='h6' gutterBottom>Loading...</Typography>}
                {status === 'failed' && <Typography variant='h6' gutterBottom>Error: {error}</Typography>}
                {status === 'succeeded' && (
                    <Box>
                        {languages.map((language) => (
                            <Chip key={language.id} label={language.name} />
                        ))}
                    </Box>
                )}
            </Box>
            <Box mt={5}>
                <Typography variant='h6' gutterBottom>
                    Add Language
                </Typography>
                <form onSubmit={handleSubmit}>
                    <TextField
                        fullWidth
                        label='Language Name'
                        margin='normal'
                        value={languageName}
                        onChange={(e) => setLanguageName(e.target.value)}
                        required
                    />
                    <Button
                        type='submit'
                        variant='contained'
                        color='primary'
                        fullWidth
                        disabled={status === 'loading'}>
                        {status === 'loading' ? 'Logging in...' : 'Send'}
                    </Button>
                </form>
            </Box>
            <Box mt={5}>
            <FormControl fullWidth margin='normal'>
                <InputLabel>Partner's Target Language</InputLabel>
