import { useSelector, useDispatch } from 'react-redux';
import { getAllLanguages } from '../services/languageControlService';
import { useEffect, useState } from 'react';
import { Container, Box, Typography, Chip, TextField , Button} from '@mui/material';
import { addLanguage } from '../services/languageControlService';

const LanguageControlPage = () => {
    const dispatch = useDispatch();
    const { languages, status, error } = useSelector((state) => state.langControl);

    const [languageName, setLanguageName] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        dispatch(addLanguage(languageName));
    };

    useEffect(() => {
        dispatch(getAllLanguages());
    }, [dispatch]);

    return (
        <Container maxWidth='sm'>
            <Box mt={5}>
                <Typography variant='h4' gutterBottom>
                    Languages
                </Typography>
                {status === 'loading' && <Typography variant='h6' gutterBottom>Loading...</Typography>}
                {status === 'failed' && <Typography variant='h6' gutterBottom>Error: {error}</Typography>}
                {status === 'succeeded' && (
                    <Box>
                        {languages.map((language) => (
                            <Chip key={language.id} label={language.name} />
                        ))}
                    </Box>
                )}
            </Box>
            <Box mt={5}>
                <Typography variant='h6' gutterBottom>
                    Add Language
                </Typography>
                <form onSubmit={handleSubmit}>
                    <TextField
                        fullWidth
                        label='Language Name'
                        margin='normal'
                        value={languageName}
                        onChange={(e) => setLanguageName(e.target.value)}
                        required
                    />

                </form>
            </Box>
        </Container>
    );
}

export default LanguageControlPage;
