import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import {
    Chip,
    Box,
    Typography,
    Card,
    CardContent,
    Button,
    Autocomplete,
    TextField,
    Snackbar,
    Alert,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { getMe } from '../services/userService';

const API_URL = process.env.REACT_APP_API_URL;

const ProfilePage = () => {
    const user = useSelector((state) => state.users.list);
    const token = useSelector((state) => state.auth.token)
    const availableLanguages = useSelector((state) => state.users.allLang);

    const native = user.languages?.filter(
        (language) => language.type === 'native'
    );

    const target = user.languages?.filter(
        (language) => language.type === 'target'
    );
    const dispatch = useDispatch();
    const navigate = useNavigate();

    const [nativeLanguages, setNativeLanguages] = useState([]);
    const [targetLanguages, setTargetLanguages] = useState([]);
    const [success, setSuccess] = useState(false);

    const toSendNative = nativeLanguages.map(
        (lang) => availableLanguages.find((obj) => obj.name === lang).id
    );
    const toSendTarget = targetLanguages.map(
        (lang) => availableLanguages.find((obj) => obj.name === lang).id
    );

    if (!user) {
        return (
            <Box sx={{ p: 3 }}>
                <Typography variant='h6'>You are not logged in.</Typography>
                <Button
                    variant='contained'
                    onClick={() => navigate('/login')}
                    sx={{ mt: 2 }}>
                    Login
                </Button>
            </Box>
        );
    }

    const handleSave = async () => {
        try {
            await axios.put(
                `${API_URL}/users/me/languages`,
                {
                    native: toSendNative,
                    target: toSendTarget,
                },
                {
                    headers: {
                        Authorization: `Bearer ${localStorage.getItem(
                            'token'
                        )}`,
                    },
                }
            );
            dispatch(getMe(token)); // update user in Redux
            setSuccess(true);
            setNativeLanguages([])
            setTargetLanguages([])
        } catch (error) {
            console.error('Update failed:', error);
        }
    };

    return (
        <Box textAlign={'center'} sx={{ p: 3 }}>
            <Typography variant='h5' gutterBottom>
                My Profile
            </Typography>

            <Card>
                <CardContent>
                    <Typography variant='h6'>{user.full_name}</Typography>
                    <Typography>Email: {user.email}</Typography>

                    <Box sx={{ mt: 3 }}>
                        <Autocomplete
                            multiple
                            options={availableLanguages?.map(
                                (lang) => lang.name
                            )}
                            value={nativeLanguages}
                            onChange={(e, value) => setNativeLanguages(value)}
                            renderInput={(params) => (
                                <TextField
                                    {...params}
                                    label='Select native languages'
                                />
                            )}
                            sx={{ mt: 1 }}
                        />
                        <Box sx={{ mt: 2 }}>
                            <Typography variant='subtitle1'>
                                Native Languages:
                            </Typography>
                            {native?.map((lang) => (
                                <Chip
                                    key={lang.language_id}
                                    label={lang.name}
                                    sx={{ mr: 1, mt: 1 }}
                                />
                            ))}
                        </Box>
                    </Box>

                    <Box sx={{ mt: 3 }}>
                        <Autocomplete
                            multiple
                            options={availableLanguages?.map(
                                (lang) => lang.name
                            )}
                            value={targetLanguages}
                            onChange={(e, value) => setTargetLanguages(value)}
                            renderInput={(params) => (
                                <TextField
                                    {...params}
                                    label='Select target languages'
                                />
                            )}
                            sx={{ mt: 1 }}
                        />
                        <Box sx={{ mt: 2 }}>
                            <Typography variant='subtitle1'>
                                Target Languages:
                            </Typography>
                            {target?.map((lang) => (
                                <Chip
                                    key={lang.language_id}
                                    label={lang.name}
                                    sx={{ mr: 1, mt: 1 }}
                                />
                            ))}
                        </Box>
                    </Box>

                    <Button
                        variant='contained'
                        onClick={handleSave}
                        sx={{ mt: 3 }}>
                        Save Changes
                    </Button>
                </CardContent>
            </Card>

            <Snackbar
                open={success}
                autoHideDuration={3000}
                onClose={() => setSuccess(false)}>
                <Alert
                    onClose={() => setSuccess(false)}
                    severity='success'
                    sx={{ width: '100%' }}
                    textAlign={'center'}>
                    Profile updated!
                </Alert>
            </Snackbar>
        </Box>
    );
};

export default ProfilePage;
